mapper.select:
SELECT TITLE, SEQ, CONTENT, PIC1,CATEGORY, NAME, like_count FROM ( SELECT ROW_NUMBER() OVER (ORDER BY E.REG_DATE DESC) as rnum, TITLE, E.SEQ, CONTENT, PIC1, (SELECT TITLE FROM EDITORCATEGORY WHERE SEQ = E.TARGET_CATEGORY_SEQ) AS CATEGORY, T.NAME, (select count(*) from liketable C where E.seq = C.target_bbs_seq and C.bbs_category=3) as like_count FROM EDITOR E, TRAVELER T WHERE T.SEQ = E.TARGET_USER_SEQ
AND TITLE like '%'||#{s_keyword}||'%' AND CONTENT like '%'||#{s_keyword}||'%'
) A WHERE rnum between #{start} AND #{end}

SELECT SEQ, TARGET_USER_SEQ, TITLE, LOCATION, STATUS, TO_DATE, FROM_DATE, RANGE, TOTAL_COST, REG_DATE, LAST_UPDATED, PAPER, NAME FROM (SELECT ROW_NUMBER() OVER (ORDER BY P.REG_DATE DESC) as rnum, P.SEQ, P.TARGET_USER_SEQ, P.TITLE, P.LOCATION, P.STATUS, P.TO_DATE, P.FROM_DATE, P.RANGE, P.TOTAL_COST, P.REG_DATE, P.LAST_UPDATED, P.PAPER, T.NAME FROM TRAVELER T, PLANER P WHERE T.SEQ = P.TARGET_USER_SEQ AND P.STATUS NOT IN(SELECT STATUS FROM PLANER WHERE STATUS=2 OR STATUS=3 OR STATUS=4)
AND TITLE like '%'||#{s_keyword}||'%' AND LOCATION like '%'||#{s_keyword}||'%'
) A WHERE rnum between ${start} AND ${end} ORDER BY REG_DATE DESC
